-- ========== 2014 ==========
-- ========== 2015 ==========
-- ========== 2016 ==========
-- ========== 2017 ==========
-- ========== 2018 ==========
-- ========== 2019 ==========
-- ========== 2020 ==========
-- ========== 2021 ==========
-- ========== 2022 ==========
-- ========== 2023 ==========
-- ========== 2024 ==========

MERGE INTO Discounts d
USING (SELECT 'D000' AS discountId,
              'No Discount' AS discountName,
              0 AS discountRate,
              DATE '2004-07-01' AS startDate,
              DATE '2024-06-30' AS endDate
       FROM dual) x
ON (d.discountId = x.discountId)
WHEN NOT MATCHED THEN
  INSERT (discountId, discountName, discountRate, discountStart, discountEnd)
  VALUES (x.discountId, x.discountName, x.discountRate, x.startDate, x.endDate);

COMMIT;

